chaty(String Curl, Color Ccolor, String Ctitle, String Cavatar, String Cmsg,
    String Ctime) {
  return ListTile(
    tileColor: Colors.white,
    leading: CircleAvatar(
      backgroundColor: Ccolor,
      backgroundImage: NetworkImage(Curl),
      // child: Text(Cavatar),
    ),
    title: Text(Ctitle),
    subtitle: Text(Cmsg),
    trailing: Text(Ctime),
  );
}

chatycall(String Curl, Color Ccolor, String Ctitle, String Cavatar, String Cmsg,
    String Ctime) {
  return ListTile(
    tileColor: Colors.white,
    leading: CircleAvatar(
      backgroundColor: Ccolor,
      backgroundImage: NetworkImage(Curl),
      // child: Text(Cavatar),
    ),
    title: Text(Ctitle,style: const TextStyle(color: Colors.red),),
    subtitle:Row(children: [
    const Icon(
      Icons.call_received,
      color: Colors.red,
    ),
    Text(Cmsg),
    ],) ,
    trailing: const Icon(Icons.call,color: Colors.green,),
  );
}